// 点的图形
var symbolImageBlue = 'data:image/png;base64,';
var symbolImageRed = 'data:image/png;base64,';

/**
 * 依据颜色和透明度创建rgba值。
 * @author Molay Chen, 大数据可视化部
 * @param {number} color 颜色的十六进制数值，0xRRGGBB形式
 * @param {number} alpha 透明度，取值在0-1之间
 * @return {string} 标准rgba字符串
 */
function createRgba(color, alpha) {
    return [
        "rgba(",
        color >> 16, ",",
        color >> 8 & 0xFF, ",",
        color & 0xFF, ",",
        alpha,
        ")"
    ].join("");
}

